<style>
    .cards {
        display: flex;
        justify-content: space-between;
        flex-wrap: wrap;
        gap: 15px;

    }
    article {
        width: 48%;
        box-shadow: 0px 2px 4px 0px #60617029;
        border-radius: 20px;
        padding: 5vh;
    }

    article header, article main {
        display: flex;
        justify-content: start;
        gap: 10px;
    }

    article header h5 {
        font-family: 'sf-ui-display-heavy';
        font-size: 16px;
        line-height: 146%;
        letter-spacing: 1%;
        text-transform: uppercase;
        color: #5B5B5BB2;
        margin-bottom: 3vh;
    }

     article header .text-black {
        color: #2F2F2F;
     }

    article header p {
        font-family: 'sf-ui-display-medium';
        font-size: 14px;
        line-height: 146%;
        letter-spacing: 0%;
        margin-bottom: 5vh;
        color: #2F2F2F;
    }

    article header div, article main ul {
        width: 51%;
    }

    article header img {
        height: auto;
        width: 40%;
    }

    main ul {
        list-style: none;
    }

    .p-elect, .p-gas {
        font-family: 'sf-ui-display-medium';
        border-radius: 40px;
        background: #E20C181A;
        border: 1px solid #E20C18;
        font-size: 14px;
        line-height: 28px;
        letter-spacing: 0%;
        text-align: start;
        color: #E20C18;
        padding: 6px 16px;
        margin: 0;
    }
    .p-gas {
        background: #0C49E21A;
        border: 1px solid #0C49E2;
        color: #0C49E2;
    }

    main ul li p, main ul li p span{
        font-size: 14px;
        line-height: 196%;
        letter-spacing: 0%;
        font-family: 'sf-ui-display-medium';
    }

    main ul li p span {
        color: #8C8C8C;
    }

    #item-stilema-model {
        display: flex;
        justify-content: start;
        align-items: center;
        gap: 10px;
        color: #8C8C8C;
    }

    #item-stilema-model p {
        line-height: 18px;
    }

    @media (max-width: 767px) {
        .cards {
            display: block;
        }
        article {
            width: 100%;
            margin-bottom: 2vh;
        }
        article header img {
            margin-top: 3vh;
            width: 60%;
        }
        article header, article main {
            display: block;
        }
        article header div, article main ul {
            width: 100%;
        }
        article header h5, article header p {
            margin-bottom: 2vh;
        }
    }
</style>